mapper.select:
SELECT user_id as userId, name, ip, ipAddress, header_pic as headerPic, address, activeCode, level, is_active as isActive, is_forbid as isForbid, email, sex, age, tel, qq, weixin, description, create_time as createTime, update_time as updateTime, role_id as roleId, is_delete as isDelete FROM user
and name LIKE '%${keyword}%'
ORDER BY ${order} LIMIT #{pageNo} ,#{pageSize};

SELECT user_id as userId, name, password, ip, ipAddress, header_pic as headerPic, address, activeCode, level, is_active as isActive, is_forbid as isForbid, email, sex, age, tel, qq, weixin, description, create_time as createTime, update_time as updateTime, role_id as roleId, is_delete as isDelete FROM user
and name = #{name}
and password = #{password}
or user_id = #{userId}
